const {expect} = require('chai');
const {move} = require('../days/day2');
const {getCombination} = require('../days/day2');

var line1 = '';
var line2 = '';
var line3 = '';
var line4 = '';
var line5 = '';
var arrayLines = [line1, line2, line3, line4, line5];

describe('getCombination()', () => {
  it('is a function', () => {
    expect(getCombination).to.be.a('function');
  });
  it('returns a number', () => {
    expect(getCombination([])).to.be.a('string');
  });
  it('takes on arg', () => {
    expect(getCombination.length).to.equal(1);
  });
  it('returns 1', () => {
    expect(getCombination(['UL'])).to.equal('1');
  });
  xit('returns 9', () => {
    expect(getCombination(['RDRD'])).to.equal('9');
  });
  xit('returns 19', () => {
    expect(getCombination(['UL', 'RDRD'])).to.equal('19');
  });
  it('returns correct single move value', () => {
    expect(move('5', 'U')).to.equal('2');
  });
  it('returns correct single move value', () => {
    expect(move('5', 'D')).to.equal('8');
  });
  it('returns correct single move value', () => {
    expect(move('5', 'L')).to.equal('4');
  });
  it('returns correct single move value', () => {
    expect(move('5', 'R')).to.equal('6');
  });
  it('returns correct single move value', () => {
    expect(move('3', 'U')).to.equal('3');
  });
  it('returns correct single move value', () => {
    expect(move('3', 'D')).to.equal('6');
  });
  it('returns correct single move value', () => {
    expect(move('3', 'L')).to.equal('2');
  });
  it('returns correct single move value', () => {
    expect(move('3', 'R')).to.equal('3');
  });



});
